mapper.select:
select od.id as orderId, od.created_time as createdTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, od.is_comment as isComment from orders od LEFT JOIN order_status os ON os.order_id = od.id where od.user_id = #{paramsMap.userId} and od.is_delete = 0
and os.order_status = #{paramsMap.orderStatus}
ORDER BY od.created_time ASC

select od.id as orderId, od.created_time as createdTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, oi.item_id as itemId, oi.item_name as itemName, oi.item_img as itemImg, oi.item_spec_name as itemSpecName, oi.price as price from orders od LEFT JOIN order_status os ON os.order_id = od.id LEFT JOIN order_items oi ON os.order_id = oi.order_id where od.user_id = #{paramsMap.userId} and od.is_delete = 0
os.order_status = #{paramsMap.orderStatus}
ORDER BY od.created_time ASC

SELECT
    COUNT( 1 )
FROM
    orders o
        LEFT JOIN order_status os ON o.id = os.order_id
WHERE
    o.user_id = #{paramsMap.userId}
    AND os.order_status = #{paramsMap.orderStatus}

        AND o.is_comment = #{paramsMap.isComment}
